            to={`/actualizar/${d.id} `}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 mx-1 rounded"
          >
            Leer
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Leer;
